create table "COMPANY" (
  "ID" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,
  "NAME" VARCHAR NOT NULL
);
create table "COMPUTER" (
  "ID" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,
  "NAME" VARCHAR NOT NULL,
  "INTRODUCED" DATE,
  "DISCONTINUED" DATE,
  "COMPANY_ID" INTEGER,
  FOREIGN KEY ("COMPANY_ID") REFERENCES "COMPANY"("ID")
);
